// src/components/UserDetailsAxios.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const UserDetailsAxios = () => {

  const { userId } = useParams(); // Captura el userId de la URL
  const [userData, setUserData] = useState(null);

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/users/${userId}`);
      setUser(response.data);
    } catch (err) {
      console.log(err)
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUser();
  }, [userId]);

  if (loading) return <p>Cargando detalles del usuario...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Detalles del Usuario (con Axios)</h2>
      {user ? (
        <div>
          <p><strong>Nombre:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Saldo:</strong> {user.balance}</p>
          {/* Otros detalles del usuario */}
        </div>
      ) : (
        <p>No se encontr√≥ el usuario.</p>
      )}
    </div>
  );
};

export default UserDetailsAxios;
