// src/components/UserDetailsAxios.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const UserDetailsAxios = () => {

  const { userId } = useParams(); // Captura el userId de la URL
  const [userData, setUserData] = useState(null);

  const [user, setUser] = useState(null);
  const [funds, setFunds] = useState(null);
  const [bitacora, setBitacora] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/users/`);
      setUser(response.data);

    } catch (err) {
      setError(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUser();
  }, [userId]);

  if (loading) return <p>Cargando detalles del usuario...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Detalles del Usuario (con Axios)</h2>
      {user ? (
        <div>
          <a class="nav-link" href="`/${user.idusuario}`" style={{"cursor":"pointer"}}>
                <i class="menu-icon  mdi mdi-chart-line"></i>
                <span class="menu-title">Historial de eventos</span>
          </a>
          <p><strong>id:</strong> {user.idusuario}</p>
          <p><strong>Nombre:</strong> {user.nombres}</p>
          <p><strong>Identificacion:</strong> {user.identificacion}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Saldo:</strong> {user.saldo_base}</p>
          {/* Otros detalles del usuario */}
        </div>
      ) : (
        <p>No se encontr√≥ el usuario.</p>
      )}
    </div>
  );
};

export default UserDetailsAxios;
